## Process strategies ----

# Export strategies into individual RDS files under data
for(i in seq_len(length(strategy_list))) {

  # Obtain a single strategy set from the list
  strategy_set = strategy_list[[i]]

  # Obtain strategy metadata
  strategy_items = nrow(strategy_set)
  strategy_attributes = ncol(strategy_set)
  strategy_amount = dim(strategy_set)[3]

  # Apply strategy naming scheme
  strategy_name = paste0(
    "strategy_oracle",
    "_k", strategy_attributes,
    "_j", strategy_items,
    "_s", strategy_amount)

  # Assign data to the strategy_name string
  assign(x = strategy_name,
         value = strategy_set)

  # Export strategy information
  save(list = strategy_name,
       file = file.path("data", paste0(strategy_name, ".rda")),
       version = 2)

}
